/// <reference types="react-scripts" />

// Extensões de tipo para módulos sem suporte nativo ao TypeScript
declare module '*.module.css' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.module.scss' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.module.sass' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.png' {
  const src: string;
  export default src;
}

declare module '*.jpg' {
  const src: string;
  export default src;
}

declare module '*.jpeg' {
  const src: string;
  export default src;
}

declare module '*.gif' {
  const src: string;
  export default src;
}

declare module '*.svg' {
  import * as React from 'react';

  export const ReactComponent: React.FunctionComponent<React.SVGProps<SVGSVGElement>>;

  const src: string;
  export default src;
}

// Extensões para variáveis de ambiente
declare namespace NodeJS {
  interface ProcessEnv {
    NODE_ENV: 'development' | 'production' | 'test';
    REACT_APP_API_URL: string;
    REACT_APP_AUTH_TOKEN_KEY?: string;
    REACT_APP_FEATURE_NEW_UI?: string;
  }
}

// Extensão para o objeto Window
interface Window {
  __REDUX_DEVTOOLS_EXTENSION_COMPOSE__?: any; // Usando any temporariamente
}
